import React from "react";
import { useMachine } from "@xstate/react";
import { assign, createActor, createMachine, fromCallback, fromPromise, fromTransition, setup } from "xstate";
import { percentage, minutes, seconds } from "./utils";

import "./reset.css";
import "./App.css";

/**
 * Video State Machine
 */



const videoMachine = setup({
  types:{},
  actions:{
    setVideo : assign({
      video: ({event}) => event.video,
      duration: ({event}) => event.video.duration
    }),

    setElapsed : assign({
      elapsed: ({context, _event}) => context.video.currentTime
    }),

    playVideo : ({context, _event}) => {
      context.video.play();
    },

    pauseVideo : ({context, _event}) => {
      context.video.pause();
    },

    restartVideo : ({context, _event}) => {
      context.video.currentTime = 0;
      context.video.play();
      }
    },  //actions
    guards:{},
    delays:{},
    actors:{
      observador:fromCallback(({sendBack, receive}) => {
        receive ((event) => {
          if(event.type === "observador.getTime")
            {
              setTimeout(()=>{
                sendBack({ type: "setElapsed"});
               }, 500);

              setTimeout(()=>{
                sendBack({type: "pauseVideo"});
              }, 500);
            }    
        });
    }),
  },  //actors
  })
.createMachine({
  id: "video",
  initial: "loading",

  context: {
    video: null,
    duration: 0,
    elapsed: 0
  },

  states: {
    loading: {
      on: {
        "control.boton.oprime.ready": {
          target: "ready",
          actions: []
        },
        FAIL: "failure"
      }
    },
    ready: {
      entry: {
        type: "setVideo",
      },
      exit: {
        type: "pauseVideo",
      },
      invoke: {
        src: "observador",
        id: "observador",
        input: {},
      },
      initial: "paused",
      states: {
        paused: {
          entry: {
              type: "setElapsed",
            },
            exit: {
              type: "playVideo",
            },
          on: {
            "control.boton.oprime.play": {
              target: "play",
              actions: []
            }
          }
        },
        play: {
          entry: {
            type: "setElapsed",
          },
          exit: {
            type: "pauseVideo",
          },
          on: {          
            OBSERVADOR: {
              target: "playing",
              actions: "setElapsed"
            },
            "control.boton.oprime.pause": {
              target: "paused",
              actions: []
            },
            "control.boton.oprime.stop": "ended"
          }
        },
        playing: {
          entry: {
            type: "setElapsed",
          },
          exit: {
            type: "pauseVideo",
          },
          on: {          
            OBSERVADOR: {
              target: "playing",
              actions: "setElapsed"
            },
            "control.boton.oprime.pause": {
              target: "paused",
              actions: []
            },
            "control.boton.oprime.stop": "ended"
          }
        },
        ended: {
          on: {
            "control.boton.oprime.play": {
              target: "playing",
              actions: "restartVideo"
            }
          }
        }
      }
    },
    failure: {
      type: "final"
    }
  }
});


/**
 * Components
 */

export default function App() {
  const ref = React.useRef(null);
  const [current, send] = useMachine(videoMachine);
  const { duration, elapsed } = current.context;

  return (
    <div className="container">
      <video
        ref={ref}
        onCanPlay={() => {
          send({type:"control.boton.oprime.ready",  video: ref.current });
        }}
        onTimeUpdate={() => {
          send({type:"OBSERVADOR"});
        }}
        onEnded={() => {
          send({type:"control.boton.oprime.stop"});
        }}
        onError={() => {
          send({type:"FAIL"});
        }}
      >
        <source src="/fox.mp4" type="video/mp4" />
      </video>

      {["paused", "play", "playing", "ended"].some(subState =>
        current.matches({ ready: subState })
      ) && (
        <div>
          <ElapsedBar elapsed={elapsed} duration={duration} />
          <Buttons current={current} send={send} />
          <Timer elapsed={elapsed} duration={duration} />
          <pre>{ JSON.stringify(current.value, null, 2) }</pre>
        </div>
      )}
    </div>
  );
}

const Buttons = ({ current, send }) => {
  if (current.matches({ ready: "playing" })) {
    return (
      <button
        onClick={() => {
          send({type:"control.boton.oprime.pause"});
        }}
      >
        Pause
      </button>
    );
  }

  return (
    <button
      onClick={() => {
        send({type:"control.boton.oprime.play"});
      }}
    >
      Play
    </button>
  );
};

const ElapsedBar = ({ elapsed, duration }) => (
  <div className="elapsed">
    <div
      className="elapsed-bar"
      style={{ width: `${percentage(duration, elapsed)}%` }}
    />
  </div>
);

const Timer = ({ elapsed, duration }) => (
  <span className="timer">
    {minutes(elapsed)}:{seconds(elapsed)} of {minutes(duration)}:
    {seconds(duration)}
  </span>
);
