import React from "react";
import { useMachine } from "@xstate/react";
import { assign, createActor, createMachine, fromTransition } from "xstate";
import { percentage, minutes, seconds } from "./utils";

import "./reset.css";
import "./App.css";

/**
 * Video State Machine
 */

const videoOps = fromTransition((state, event) => {
  return state
}, {});

const controlActor = createActor(videoOps);



const videoMachine = createMachine({
  id: "video",
  initial: "loading",

  context: {
    video: null,
    duration: 0,
    elapsed: 0
  },

  states: {
    loading: {
      on: {
        "control.boton.oprime.ready": {
          target: "ready",
          actions: ["setVideo"]
        },
        FAIL: "failure"
      }
    },
    ready: {
      initial: "paused",
      states: {
        paused: {
          on: {
            "control.boton.oprime.play": {
              target: "playing",
              actions: ["setElapsed", "playVideo"]
            }
          }
        },
        playing: {
          on: {
            TIMING: {
              target: "playing",
              actions: "setElapsed"
            },
            "control.boton.oprime.pause": {
              target: "paused",
              actions: ["setElapsed", "pauseVideo"]
            },
            "control.boton.oprime.stop": "ended"
          }
        },
        ended: {
          on: {
            "control.boton.oprime.play": {
              target: "playing",
              actions: "restartVideo"
            }
          }
        }
      }
    },
    failure: {
      type: "final"
    }
  }
});

/**
 * Action functions
 */

const setVideo = assign({
  video: (_context, event) => event.video,
  duration: (_context, event) => event.video.duration
});

const setElapsed = assign({
  elapsed: (context, _event) => context.video.currentTime
});

const playVideo = (context, _event) => {
  context.video.play();
};

const pauseVideo = (context, _event) => {
  context.video.pause();
};

const restartVideo = (context, _event) => {
  context.video.currentTime = 0;
  context.video.play();
};

/**
 * Components
 */

export default function App() {
  const ref = React.useRef(null);
  const [current, send] = useMachine(videoMachine, {
    actions: { setVideo, setElapsed, playVideo, pauseVideo, restartVideo }
  });
  const { duration, elapsed } = current.context;

  return (
    <div className="container">
      <video
        ref={ref}
        onCanPlay={() => {
          send("control.boton.oprime.ready", { video: ref.current });
        }}
        onTimeUpdate={() => {
          send("TIMING");
        }}
        onEnded={() => {
          send("control.boton.oprime.stop");
        }}
        onError={() => {
          send("FAIL");
        }}
      >
        <source src="/fox.mp4" type="video/mp4" />
      </video>

      {["paused", "playing", "ended"].some(subState =>
        current.matches({ ready: subState })
      ) && (
        <div>
          <ElapsedBar elapsed={elapsed} duration={duration} />
          <Buttons current={current} send={send} />
          <Timer elapsed={elapsed} duration={duration} />
        </div>
      )}
    </div>
  );
}

const Buttons = ({ current, send }) => {
  if (current.matches({ ready: "playing" })) {
    return (
      <button
        onClick={() => {
          send("control.boton.oprime.pause");
        }}
      >
        Pause
      </button>
    );
  }

  return (
    <button
      onClick={() => {
        send("control.boton.oprime.play");
      }}
    >
      Play
    </button>
  );
};

const ElapsedBar = ({ elapsed, duration }) => (
  <div className="elapsed">
    <div
      className="elapsed-bar"
      style={{ width: `${percentage(duration, elapsed)}%` }}
    />
  </div>
);

const Timer = ({ elapsed, duration }) => (
  <span className="timer">
    {minutes(elapsed)}:{seconds(elapsed)} of {minutes(duration)}:
    {seconds(duration)}
  </span>
);
