import React from "react";
import { useMachine} from "@xstate/react";
import { assign, createActor, createMachine, fromCallback, fromPromise, fromTransition, setup, sendTo, fromObservable } from "xstate";
import { percentage, minutes, seconds } from "./utils";
import {Logger, ConsoleLogger} from 'react-console-logger';



import "./reset.css";
import "./App.css";


const pino = require("pino");
const logger = pino({
  transport: {
    target: "pino-pretty",
  },
});

logger.info('Hello World');

const myLogger = new Logger();



/**
 * Video State Machine
 */



const videoMachine = setup({
  types:{},

  actions:{

    setVideo :  
       assign({
        video: ({event}) => event.video,
        duration: ({event}) => event.video.duration
      }),


    setElapsed : assign({
      elapsed: ({context, _event}) => context.video.currentTime,
    }),

    setTime : assign({
      elapsed: ({context, _event}) => context.video.currentTime
    }),

    playVideo : ({context, _event}) => {
      context.video.play();
      myLogger.info("Reproduciendo video ");
    },

    logAction : ({context, _event}) => {
      myLogger.info("Preparando video ");
    },

    pauseVideo : ({context, _event}) => {
      context.video.pause();
    },

    progressVideo : ({context, _event}) => {
      myLogger.warn("Avance:"+context.avance);
    },

    setAvance : assign({
      avance: ({context, _event}) => Math.floor((context.video.currentTime/context.video.duration)*100),
    }),

    restartVideo : ({context, _event}) => {
      context.video.currentTime = 0;
      context.video.play();
      context.video.numVideos = context.video.numVideos +1;
    },

    setNumVideos : ({context, _event}) => {
      context.video.numVideos = context.video.numVideos +1;
    },

    },
    guards:{},
    delays:{},
    actors:{
      iniciador: fromCallback(({sendBack, receive}) => {
        receive(event => {
          if (event.type === 'setVideo') {
            sendBack({type:"setVideo"})
          }
          else if (event.type === 'setNumVideos') {
            sendBack({type:"setNumVideos"})
          } 
        })

      }),

      observador: fromObservable(({input}) => {
        setInterval(input.interval)          
      }),

    }
})
.createMachine({
  id: "video",
  initial: "loading",

  context: {
    video: null,
    duration: 0,
    elapsed: 0, 
    numVideos:0,
    avance:0,
  },

  states: {
    loading: {
      invoke: {
        id:"observador",
        src: "iniciador",
      },      
      on: {
        "control.boton.oprime.ready": {
          target: "ready",
          actions: ["setVideo", "logAction"]
        },
        FAIL: "failure"
      }
    },
    ready: {
      initial: "paused",
      states: {
        paused: {
          entry: {
            type: "setElapsed",
          },
          exit: {
            type: "playVideo",
          },
          on: {
            "control.boton.oprime.play": {
              target: "playing",
              actions: []
            }
          }
        },
        playing: {
          invoke: {
            src: fromPromise(async() => {
              await new Promise((r) => setTimeout(r, 20000));
              return {status:200}
            }),
            onDone: {
              target: 'playing'
            }
          }, 

          entry: {
            type: "setElapsed",
          },
          exit: {
            type: "pauseVideo",
          },
          on: {
            TIMING: {
              target: "playing",
              actions: ["setElapsed", "setAvance", "progressVideo"]
            },
            "control.boton.oprime.pause": {
              target: "paused",
              actions: []
            },
            "control.boton.oprime.stop": "ended"
          }
        },
        ended: {
          invoke: {
            id:"observador",
            src: "iniciador",             
          },  
          on: {
            "setNumVideos":{
              actions:sendTo("iniciador", ({event}) =>({
                type:"setNumVideos",
              }))
            },
            "control.boton.oprime.play": {
              target: "playing",
              actions: "restartVideo"
            }
          }
        }
      }
    },
    failure: {
      type: "final"
    }
  }
});


/**
 * Components
 */

export default function App() {
  const ref = React.useRef(null);
  const [current, send] = useMachine(videoMachine);
  const { duration, elapsed, avance } = current.context;




  return (
    <div className="container">
      <video
        ref={ref}
        onCanPlay={() => {
          send({type:"control.boton.oprime.ready",  video: ref.current });
        }}
        onTimeUpdate={() => {
          send({type:"TIMING",  video: ref.current});
        }}
        onEnded={() => {
          send({type:"control.boton.oprime.stop"});
        }}
        onError={() => {
          send({type:"FAIL"});
        }}
      >
        <source src="/fox.mp4" type="video/mp4" />
      </video>

      {["paused", "playing", "ended"].some(subState =>
        current.matches({ ready: subState })
      ) && (
        <div>
          <ElapsedBar elapsed={elapsed} duration={duration} />
          <Buttons current={current} send={send} />
          <Timer elapsed={elapsed} duration={duration} avance = {avance} />
        </div>


      )}

      <div>
        <ConsoleLogger   logger={myLogger} />
      </div>
    </div>
  );
}

const Buttons = ({ current, send }) => {
  if (current.matches({ ready: "playing" })) {
    return (
      <button
        onClick={() => {
          send({type:"control.boton.oprime.pause"});
        }}
      >
        Pause
      </button>
    );
  }

  return (
    <button
      onClick={() => {
        send({type:"control.boton.oprime.play"});
      }}
    >
      Play
    </button>
  );
};

const ElapsedBar = ({ elapsed, duration }) => (
  <div className="elapsed">
    <div
      className="elapsed-bar"
      style={{ width: `${percentage(duration, elapsed)}%` }}
    />
  </div>
);

const Timer = ({ elapsed, duration }) => (
  <span className="timer">
    {minutes(elapsed)}:{seconds(elapsed)} of {minutes(duration)}:
    {seconds(duration)}
  </span>
);
